document.addEventListener('DOMContentLoaded', () => {
    // --- Configuration ---
    const primaryColor = "rgb(0, 0, 0)";       // Main background
    const secondaryColor = "rgb(14, 14, 14)";   // Input/border background
    const textColor = "white";                // Default text color
    const highlightColor = "#fff";            // Shadow/Highlight color
    const focusColor = "rgb(0, 100, 200)";    // Color for focus indicator (e.g., blue glow)
    const defaultFont = "'Courier New', Courier, monospace";

    // --- Elements ---
    const body = document.body;
    // Get ALL divs. If you only want to style specific divs, adjust this selector
    // e.g., document.querySelectorAll('.styled-div') or document.getElementById('myDiv')
    const divs = document.querySelectorAll('div');
    const formElements = document.querySelectorAll('input, button, textarea');

    // --- Base Body Styles ---
    body.style.backgroundColor = primaryColor; // Use backgroundColor for clarity
    body.style.color = textColor;
    body.style.fontFamily = defaultFont;
    body.style.margin = "0";
    body.style.padding = "0";

    // --- Style Form Elements (Inputs, Buttons, Textareas) ---
    formElements.forEach(el => {
        // Basic Appearance
        el.style.backgroundColor = secondaryColor;
        el.style.color = textColor; // Explicitly set text color for inputs
        el.style.fontFamily = defaultFont; // Explicitly set font for inputs
        el.style.border = "1px solid " + secondaryColor; // Use a subtle border instead of none initially
        el.style.borderRadius = "10px";
        el.style.padding = "10px";
        el.style.margin = "10px";
        el.style.boxShadow = `0px 0px 5px ${highlightColor}`; // Default shadow

        // Remove default outline - **REPLACED** with focus style below
        el.style.outline = "none";

        // Add custom focus style for accessibility
        el.addEventListener('focus', () => {
            // Make the shadow more prominent and change color on focus
            el.style.boxShadow = `0px 0px 8px 2px ${focusColor}`;
            el.style.borderColor = focusColor; // Optional: change border color too
        });

        // Revert to default style on blur (when focus is lost)
        el.addEventListener('blur', () => {
            el.style.boxShadow = `0px 0px 5px ${highlightColor}`;
            el.style.borderColor = secondaryColor; // Revert border color
        });
    });

    // --- Style Divs ---
    divs.forEach(div => { // Loop through all selected divs
        div.style.backgroundColor = primaryColor;
        // Combine border properties for brevity
        div.style.border = `solid 5px ${secondaryColor}`;
        div.style.margin = "10px";
        div.style.padding = "10px";
        div.style.borderRadius = "10px";
        div.style.boxShadow = `0px 0px 5px ${highlightColor}`;
        // Font and text color will be inherited from the body
    });

    // Note: Removed the universal '*' selector for better performance and less conflict.
    // Note: Removed the unused 'code' variable.
    // Note: Added focus/blur handlers for better accessibility on form elements.
});