document.addEventListener('DOMContentLoaded', () => {
    // root config
    let primary = "rgb(0, 0, 0)";
    let secondary = "rgb(14, 14, 14)";

    // elements
    let body = document.body;
    let div = document.querySelector('div');
    let code = document.querySelector('code');

    // get all input elements (input, button, textarea)
    let inputs = document.querySelectorAll('input, button, textarea');

    // universal styles
    document.querySelectorAll('*').forEach(el => {
        el.style.fontFamily = "'Courier New', Courier, monospace";
        el.style.color = "white";
    });

    // body reset
    body.style.background = primary;
    body.style.margin = "0";
    body.style.padding = "0";

    // style form elements
    inputs.forEach(el => {
        el.style.background = secondary;
        el.style.boxShadow = "0px 0px 5px #fff";
        el.style.borderRadius = "10px";
        el.style.border = "none";
        el.style.outline = "none";
        el.style.padding = "10px";
        el.style.margin = "10px";
        el.style.color = "white"; // Ensure text color stays visible
        el.style.fontFamily = "'Courier New', Courier, monospace";
    });

    // style div
    if (div) {
        div.style.background = primary;
        div.style.border = "solid 5px";
        div.style.borderColor = secondary;
        div.style.margin = "10px";
        div.style.padding = "10px";
        div.style.borderRadius = "10px";
        div.style.boxShadow = "0px 0px 5px #fff";
    }
});
